mapper.select:
select 'false' as QUERYID,

from sys_resource
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into sys_resource (id,
path, flag,
description,  name
)
values
(#{id,jdbcType=INTEGER}, #{path,jdbcType=VARCHAR},
#{flag,jdbcType=INTEGER},
#{description,jdbcType=VARCHAR},
#{name,jdbcType=VARCHAR}
)
